class _music:
    
    def __init__(self,file_path, channel):
        try:
            wave_file = wave.open(file_path, 'rb')
            mixer.init(frequency=wave_file.getframerate())
            mixer.Channel(channel).play(mixer.Sound(file_path))
        except Exception as e:
            print(f"Error: {e}")

def startUP():
    global _toGuess
    
