def remove_button():
    global _removing
    global _show
    button_fx = _music(f"{Final_PIT_Location}//assets//Button_fx.wav",1)
    if _show == False:
        if _removing == False:
           _removing = True
       
           _status = "REMOVING BET!"
           canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
           canvas.itemconfig(tagOrId=txt_bet, fill="#FD0000")
           return       
        elif _removing == True:
            _removing = False
            _status = "ADD BET!"
            canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#6FFD00")
            canvas.itemconfig(tagOrId=txt_bet, fill="#6FFD00")
            return               
    else:
        _status = "Press Enter..."
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
        error_fx = _music(f"{Final_PIT_Location}//assets//Error2.wav",1)
    
def bet_button(_chip):
    global _money
    global _bet
    global _status
    
    button_fx = _music(f"{Final_PIT_Location}//assets//Button_fx4.wav",1)
    
    #remover process
    if _removing == True:
      
        _status = f"+{_chip}"
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#6FFD00")
        canvas.itemconfig(tagOrId=txt_bet, fill="#FD0000")
        if _bet < _chip:

            _status = "Not Enough\nBet Money!"
            canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
            canvas.itemconfig(tagOrId=txt_money, text=f"${_money}", fill=f"white")
            error_fx = _music(f"{Final_PIT_Location}//assets//Error2.wav",1)
            return
    
        _money += _chip
        _bet -= _chip
        
        
        canvas.itemconfig(tagOrId=txt_money, text=f"${_money}", fill=f"#6FFD00")
        canvas.itemconfig(tagOrId=txt_bet, text=f"${_bet}")
####################################################################################################
#betting process

    if _removing == False:
        
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="white")
        canvas.itemconfig(tagOrId=txt_guess, fill="white")
        if _show == False:         

            if _chip > _money:
            
                _status =  _status = f"Com:{random.choice(not_Enough_MOney)}"
                canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
                canvas.itemconfig(tagOrId=txt_money, text=f"${_money}", fill="#FD0000")
                error_fx = _music(f"{Final_PIT_Location}//assets//Error2.wav",1)
                return
        
            _money -= _chip
            _bet += _chip
        

            _status = f"Com:{random.choice(Bet_comments)}"
            canvas.itemconfig(tagOrId=txt_money, text=f"${_money}", fill=f"#FD0000")
            canvas.itemconfig(tagOrId=txt_bet, text=f"${_bet}", fill="#6FFD00")
         
        else:
            _status = "Press Enter..."
            canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
            canvas.itemconfig(tagOrId=txt_money,fill="white")
            canvas.itemconfig(tagOrId=txt_bet,fill="white")
            error_fx = _music(f"{Final_PIT_Location}//assets//Error.wav",1)
                               
def guess_button(_userGuess):
        global _guess
        global _status
        global Guess_comments
        global _show
        
        button_fx = _music(f"{Final_PIT_Location}//assets//Button_fx3.wav",1)
        
        if _show == False:
            _guess = _userGuess
            _status = f"Com:{random.choice(Guess_comments)}"
            canvas.itemconfig(tagOrId=txt_guess, text=f"{_guess}", fill="#6FFD00")
            canvas.itemconfig(tagOrId=txt_bet, text=f"${_bet}", fill="white")
            canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="white")
            canvas.itemconfig(tagOrId=txt_money,fill="white")
        else:
            _status = "Press Enter..."
            canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
            canvas.itemconfig(tagOrId=txt_money,fill="white")
            canvas.itemconfig(tagOrId=txt_bet,fill="white")
            error_fx = _music(f"{Final_PIT_Location}//assets//Error.wav",1)
            
def enter_button():
    global _guess
    global _toGuess
    global _bet
    global _money
    global _status
    global _show
    
    startUP()
    button_fx = _music(f"{Final_PIT_Location}//assets//Button_fx.wav",1)
    
    if _show == True:

        _status = "RESET"
        _guess = 0
        _toGuess = 0
        
        canvas.itemconfig(tagOrId=txt_bet, text=f"${_bet}", fill="White")
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="White")
        canvas.itemconfig(tagOrId=txt_guess, text=f"{_guess}", fill="White")
        canvas.itemconfig(tagOrId=txt_toGuess, text=f"{_toGuess}", fill="White")
        
        _show = False
        return
        
    if _bet == 0:
        _status = "No Bet Detected!"
        canvas.itemconfig(tagOrId=txt_bet, text=f"${_bet}", fill="#FD0000")
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
        error_fx = _music(f"{Final_PIT_Location}//assets//Error.wav",1)
        
        return
    
    if _guess == 0:
        _status = "No Guess number \nDetected!"
        canvas.itemconfig(tagOrId=txt_guess, fill="#FD0000")
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FD0000")
        canvas.itemconfig(tagOrId=txt_money,fill="white")
        canvas.itemconfig(tagOrId=txt_bet,fill="white")
        error_fx = _music(f"{Final_PIT_Location}//assets//Error.wav",1)

        return
    
    if _guess == _toGuess:
        
        _money += _bet*2
        
        _status = f"You WIN! ${_bet*2}"
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}", fill="#FFD600")
        canvas.itemconfig(tagOrId=txt_toGuess, text=f"{_toGuess}")
        canvas.itemconfig(tagOrId=txt_money, text=f"${_money}",fill="#FFD600")
        canvas.itemconfig(tagOrId=txt_bet, text=f"${_bet}")
        

        event_fx = _music(f"{Final_PIT_Location}//assets//Win_coin.wav",2)
        
        _show = True
        
    else:

        _status = f"You LOSE! ${_bet}"
        canvas.itemconfig(tagOrId=txt_status, text=f"{_status}",fill="#FD0000")
        canvas.itemconfig(tagOrId=txt_toGuess, text=f"{_toGuess}")
        
        event_fx = _music(f"{Final_PIT_Location}//assets//SAD.wav",2)
        
        _bet = 0
        
        canvas.itemconfig(tagOrId=txt_guess,fill="#FD0000")
        canvas.itemconfig(tagOrId=txt_money, text=f"${_money}", fill="white")
        canvas.itemconfig(tagOrId=txt_bet, text=f"${_bet}",fill="#FD0000")
        
        _show = True               
    
